import React, { Component } from "react";
import {
  Divider,
  Grid,
  Image,
  Segment,
  Advertisement,
  Accordion, Container, Icon
} from "semantic-ui-react";


export default class allCampaings extends Component{

 
    state = { activeIndex: 0 };

  handleClick = (e, titleProps) => {
    const { index } = titleProps;
    const { activeIndex } = this.state;
    const newIndex = activeIndex === index ? -1 : index;

    this.setState({ activeIndex: newIndex });
  };

  render() {
    const { activeIndex } = this.state;

    const containerstyle=
    {
      textAlign:"left"
    }

  const image1 = {
    width: "250px",
    height: "400px",
  };

  const image2 = {
    width: "440px",
    height: "360px",
  };

  const image3 = {
    width: "440px",
    height: "360px",
  };

  const image4 = {
    textAlign: "center",
  };
  return (
    //     <div>
    //     <Advertisement unit='vertical rectangle' test='Vertical Rectangle' />
    //   </div>

    <div>
      <Grid>
        <Grid.Row>
          <Image
            style={image1}
            src="images/discount6.jpg"
          />

          <Image
            style={image2}
            src="images/discount.jpg"
          />

          <Image
            style={image3}
            src="images/discount2.png"
          />
        </Grid.Row>

        <Grid.Row >
        <Image
            style={image1}
            src= "images/bosekran.PNG "
          />

          <Image
            style={image2}
            src="images/discount3.png"
          />

          <Image
            style={image3}
            src="images/discount4.jpg"
          />
        </Grid.Row>

        <Grid.Row >
        <Image
            style={image1}
            src= "images/bosekran.PNG "
          />

          <Image
            style={image2}
            src="images/discount5.jpg"
          />

          <Image
            style={image3}
            src= "images/discount7.jpg"
          />
          </Grid.Row>
      </Grid>

      <Container style={containerstyle}>
      <Accordion fluid styled>
        <Accordion.Title
          active={activeIndex === 0}
          index={0}
          onClick={this.handleClick}
        >
          <Icon name="dropdown" />
          What is a dog?
        </Accordion.Title>
        <Accordion.Content active={activeIndex === 0}>
          <p>
            A dog is a type of domesticated animal. Known for its loyalty and
            faithfulness, it can be found as a welcome guest in many households
            across the world.
          </p>
        </Accordion.Content>

        <Accordion.Title
          active={activeIndex === 1}
          index={1}
          onClick={this.handleClick}
        >
          <Icon name="dropdown" />
          What kinds of dogs are there?
        </Accordion.Title>
        <Accordion.Content active={activeIndex === 1}>
          <p>
            There are many breeds of dogs. Each breed varies in size and
            temperament. Owners often select a breed of dog that they find to be
            compatible with their own lifestyle and desires from a companion.
          </p>
        </Accordion.Content>
        <Accordion.Title
          active={activeIndex === 2}
          index={2}
          onClick={this.handleClick}
        >
          <Icon name="dropdown" />
          How do you acquire a dog?
        </Accordion.Title>
        <Accordion.Content active={activeIndex === 2}>
          <p>
            Three common ways for a prospective owner to acquire a dog is from
            pet shops, private owners, or shelters.
          </p>
          <p>
            A pet shop may be the most convenient way to buy a dog. Buying a dog
            from a private owner allows you to assess the pedigree and
            upbringing of your dog before choosing to take it home. Lastly,
            finding your dog from a shelter, helps give a good home to a dog who
            may not find one so readily.
          </p>
        </Accordion.Content>
      </Accordion>
      </Container>
    </div>
    //     <Grid verticalAlign='bottom' columns={3}>
    //     <Grid.Row>
    //       <Grid.Column>
    //         <Image style={image1} src='https://react.semantic-ui.com/images/wireframe/image.png' />
    //       </Grid.Column>
    //       <Grid.Column>
    //         <Image style={image2}  src='https://react.semantic-ui.com/images/wireframe/image.png' />
    //       </Grid.Column>
    //     </Grid.Row>
    //   </Grid>
  );
  }
}
