import React from "react";
import { Button, Container, Dropdown, Image, Menu, Input } from "semantic-ui-react";

export default function LoginScreen() {
  return (
    <div>
      <Menu inverted fixed="top" size="massive" fluid widths={4}>
        <Container>
          <Image src="images/naxus12.PNG" size="small" />
           <Menu.Item>
            <h3>Ara</h3>
          </Menu.Item> 
          <Menu.Item>
            <Input text="Ara" className="icon" icon="search" placeholder="Search..." />
          </Menu.Item>

          <Menu.Menu position="right">
            <Dropdown item text="Hesabım">
              <Dropdown.Menu>
                <Dropdown.Item>
                  <Button size="tiny">Giriş Yap</Button>
                </Dropdown.Item>
                <Dropdown.Item>
                  <Button size="tiny">Kayıt ol</Button>
                </Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          </Menu.Menu>
        </Container>
      </Menu>
    </div>

    // <div style={divstyle}>
    //   <Menu inverted fixed="top">
    //     <Container>
    //       <Menu.Item name="editorials">Editorials</Menu.Item>
    //       <Menu.Item name="reviews">Reviews</Menu.Item>
    //       <Menu.Item name="upcomingEvents">Upcoming Events</Menu.Item>
    //       </Container>
    //   </Menu>
    // </div>
  );
}
