import React from "react";
import {
  Divider,
  Grid,
  Image,
  Segment,
  Advertisement,
  List,
  Icon,
} from "semantic-ui-react";

export default function allCampaings() {
  const containerstyle = {
    textAlign: "left",
  };
  const image1 = {
    width: "250px",
    height: "400px",
  };

  const image2 = {
    width: "440px",
    height: "360px",
  };

  const image3 = {
    width: "440px",
    height: "360px",
  };

  const image4 = {
    textAlign: "center",
  };
  return (
    //     <div>
    //     <Advertisement unit='vertical rectangle' test='Vertical Rectangle' />
    //   </div>

    <div>
      <Grid>
        <Grid.Row>
          <Image style={image1} src="images/discount6.jpg" />

          <Image style={image2} src="images/discount.jpg" />

          <Image style={image3} src="images/discount2.png" />
        </Grid.Row>

        <Grid.Row>
          <Image style={image1} src="images/bosekran.PNG " />

          <Image style={image2} src="images/discount3.png" />

          <Image style={image3} src="images/discount4.jpg" />
        </Grid.Row>

        <Grid.Row>
          <Image style={image1} src="images/bosekran.PNG " />

          <Image style={image2} src="images/discount5.jpg" />

          <Image style={image3} src="images/discount7.jpg" />
        </Grid.Row>
      </Grid>

      <List style={containerstyle} size="large">
        <h1>Kampanyalar Hakkında Genel Bilgilendirme</h1>
        <List.Item>
          {" "}
          <Icon name="arrow right" size="large" color="purple" /> Kampanyalar
          başka kampanyalarla, kupon ve sepet kurgularıyla
          birleştirilememektedir
        </List.Item>
        <List.Item>
          {" "}
          <Icon name="arrow right" size="large" color="purple" />
          MediaMarkt tarafından düzenlenen tüm kampanyalar tüketicilere
          yöneliktir.
        </List.Item>
        <List.Item>
          {" "}
          <Icon name="arrow right" size="large" color="purple" />
          Kampanyalardan ticari gaye ile yararlanıldığı ve ticari alımların
          tespiti halinde kampanya kapsamındaki sipariş iptal edilebilir.{" "}
        </List.Item>
        <List.Item>
          {" "}
          <Icon name="arrow right" size="large" color="purple" />
          MediaMarkt; kampanyalar sırasında oluşabilecek öngörülemeyen
          değişiklikler nedeniyle ürün , fiyat değişimi yapma ve kampanyayı son
          erdirme hakkını saklı tutar.
        </List.Item>
      </List>
    </div>
  );
}
