import React, { Component } from "react";
import { Image, Grid, Dropdown, Accordion, Icon } from "semantic-ui-react";

export default class brands extends Component {
  state = { activeIndex: 0 };

  handleClick = (e, titleProps) => {
    const { index } = titleProps;
    const { activeIndex } = this.state;
    const newIndex = activeIndex === index ? -1 : index;

    this.setState({ activeIndex: newIndex });
  };
  render() {
    const { activeIndex } = this.state;
    const friendOptions = [
      {
        key: "Acer",
        text: "Acer",
        value: "Acer",
      },
      {
        key: "Adata",
        text: "Adata",
        value: "Adata",
      },
      {
        key: "Apple",
        text: "Apple",
        value: "Apple",
      },
    ];

    return (
      <div>
        <Grid>
          <Grid.Column width={3}>
            <h4>
              <b>Tüm Markalar </b>{" "}
            </h4>
            <Dropdown
              placeholder="Marka seçiniz"
              openOnFocus
              selection
              options={friendOptions}
            />
            <br />
            <br />
            <Accordion styled  >
              <Accordion.Title
                active={activeIndex === 0}
                index={0}
                onClick={this.handleClick}
              >
                <Icon name="dropdown" />
               A B
              </Accordion.Title>
              <Accordion.Content active={activeIndex === 0}>
                <p>
                  Acer 
                </p>
              </Accordion.Content>

              <Accordion.Title 
                active={activeIndex === 1}
                index={1}
                onClick={this.handleClick}
                          >
                <Icon name="dropdown" />
                C D E
              </Accordion.Title>
              <Accordion.Content active={activeIndex === 1}>
                <p >
                 c d d e e e e 
                </p>
              </Accordion.Content>

              <Accordion.Title
                active={activeIndex === 2}
                index={2}
                onClick={this.handleClick}
              >
                <Icon name="dropdown" />
                How do you acquire a dog?
              </Accordion.Title>
              <Accordion.Content active={activeIndex === 2}>
                <p>
                  Three common ways for a prospective owner to acquire a dog is
                  from pet shops, private owners, or shelters.
                </p>
                <p>
                  A pet shop may be the most convenient way to buy a dog. Buying
                  a dog from a private owner allows you to assess the pedigree
                  and upbringing of your dog before choosing to take it home.
                  Lastly, finding your dog from a shelter, helps give a good
                  home to a dog who may not find one so readily.
                </p>
              </Accordion.Content>
            </Accordion>
          </Grid.Column>
          <Grid.Column width={13}>
            <Image src="images/markalar1.PNG" centered />
          </Grid.Column>
        </Grid>
      </div>
    );
  }
}
