import React from "react";
import {
  Button,
  Container,
  Dropdown,
  Image,
  Menu,
  Input,
  Icon,
} from "semantic-ui-react";

const searchstyle = {
  width: "200px",
};

export default function LoginScreen() {
  return (
    <div>
      <Menu inverted fixed="top" size="massive" >
        <Container>
          <Menu.Item>
            <Image src="images/naxus12.PNG" size="small" />
          </Menu.Item>
          <Menu.Item>
            <Input
              size="tiny"
              className="icon"
              icon="search"
              placeholder="What are you looking for?"
            />
          </Menu.Item>
          
          <Menu.Item>
            <Button backgroundcolor="grey" inverted color="black">
              {" "}
              <Icon name="shopping cart" />
              Sepetim
            </Button>
          </Menu.Item>

          <Menu.Menu position="right">
            <Dropdown item text="Hesabım">
              <Dropdown.Menu>
                <Dropdown.Item>
                  <Button size="tiny">Giriş Yap</Button>
                </Dropdown.Item>
                <Dropdown.Item>
                  <Button size="tiny">Kayıt ol</Button>
                </Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          </Menu.Menu>
        </Container>
      </Menu>
    </div>

    // <div style={divstyle}>
    //   <Menu inverted fixed="top">
    //     <Container>
    //       <Menu.Item name="editorials">Editorials</Menu.Item>
    //       <Menu.Item name="reviews">Reviews</Menu.Item>
    //       <Menu.Item name="upcomingEvents">Upcoming Events</Menu.Item>
    //       </Container>
    //   </Menu>
    // </div>
  );
}
