import React from "react";
import {
  Button,
  Container,
  Dropdown,
  Image,
  Menu,
  Input,
} from "semantic-ui-react";

export default function LoginScreen() {
  return (
    <div>
      <Menu inverted fixed="top" size="massive" fluid widths={4}>
        <Container>
          <Image src="images/naxus12.PNG" size="small" />

          <Input
            action={{ color: "blue", content: "Search" }}
            icon="search"
            iconPosition="left"
            placeholder="Order #"
          />

          <Divider horizontal>Or</Divider>

          <Button
            color="teal"
            content="Create New Order"
            icon="add"
            labelPosition="left"
          />
          {/* <Menu.Item >

          </Menu.Item>
          <Menu.Item centered>
            <Input className="icon" icon="search" placeholder="What are you looking for?" />
          </Menu.Item> */}
          <Menu.Menu position="right">
            <Dropdown item text="Hesabım">
              <Dropdown.Menu>
                <Dropdown.Item>
                  <Button size="tiny">Giriş Yap</Button>
                </Dropdown.Item>
                <Dropdown.Item>
                  <Button size="tiny">Kayıt ol</Button>
                </Dropdown.Item>
              </Dropdown.Menu>
            </Dropdown>
          </Menu.Menu>
        </Container>
      </Menu>
    </div>

    // <div style={divstyle}>
    //   <Menu inverted fixed="top">
    //     <Container>
    //       <Menu.Item name="editorials">Editorials</Menu.Item>
    //       <Menu.Item name="reviews">Reviews</Menu.Item>
    //       <Menu.Item name="upcomingEvents">Upcoming Events</Menu.Item>
    //       </Container>
    //   </Menu>
    // </div>
  );
}
