              <b>-LOGİN-</b>
            </h1>
          </Form.Group>
          <br />
          <Form.Group style={girisyapstyle} size="lg" controlId="email">
            <h6>
              <b>E-MAİL</b>
            </h6>
            <Form.Control
              autoFocus
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </Form.Group>
          <br />
          <Form.Group style={girisyapstyle} size="lg" controlId="password">
            <h6>
              <b>ŞİFRE</b>
            </h6>

            <Form.Control
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </Form.Group>
          <br />
          <Button
            style={buttonn}
            block
            size="lg"
            type="submit"
            disabled={!validateForm()}
          >
            Login
          </Button>
        </Form>
      </Container>
      </Grid.Column>
    </Grid>

