import React, { Component } from "react";
import { Container, Icon, Menu, Image } from "semantic-ui-react";
import { Button } from "semantic-ui-react";

export default function Navi() {
  const mystyle = {
    padding: "10px"
  };
  const navistyle =
  {
    color:"white" 
  } 

  const loginbuttonsstyle=
  {
    //butonların dış boşluğu
    padding: "10px" , 
  }

  return (
    <div>
      <Menu style={{ backgroundColor: "#800080"}} fixed="top" size="large">
           <Image src='images/logo.PNG' size='small' />
            <Menu.Item name="NAXUS CLUB" style={navistyle}/> 
            <Menu.Item name="KAMPANYALAR" style={navistyle} />
            <Menu.Item name="MARKALAR" style={navistyle} />
            <Menu.Item name="HİZMETLER" style={navistyle} />
            <Menu.Item name="YARDIM" style={navistyle} />
            <Container className="d-flex justify-content-end">
          {/* <Button backgroundcolor = "grey" inverted color='black'> <Icon name='shopping cart' />Sepetim</Button> */}
            <Button.Group style={loginbuttonsstyle}> 
            <Button>Giriş Yap</Button>
            <Button.Or />
            <Button positive>Kaydol</Button>
            </Button.Group>
            </Container>
      </Menu> 
    </div>
  )
}
